const wifi=document.querySelector(".content.p-4");window.onload=function(){ipcRenderer.send("wifi:scan",{method:"get"})},document.querySelectorAll(".alert.alert-info").forEach((e=>{e.addEventListener("click",(e=>{document.querySelector("#name").value(e.target.children.children.children.value)}))})),ipcRenderer.on("wifi:list",((e,n)=>{wifi.innerHTML="";for(let e=0;e<n.length;e++)wifi.innerHTML+=`<button type="button" class="w-100 btn btn-lg btn-outline-primary" onclick="select('${n[e].ssid}')">\n                    <span class="fa fa-wifi"></span>&nbsp;${n[e].ssid}&nbsp;<span class="fa fa-${""!=n[e].security?"lock":"unlock"}"></span>\n                    </button>`}));const form=document.querySelector("form");function select(e){document.querySelector("#name").value=e}form.addEventListener("submit",(e=>{const n={name:document.querySelector("#name").value,pass:document.querySelector("#pass").value};ipcRenderer.send("wifi:new",n),e.preventDefault()}));